mapper.select:
SELECT 
FROM   company_customer
WHERE  id = #{id}

SELECT ,u.sex,u.name userName,u.mobile,u.state,u.work ,u.workUnit
        FROM   (SELECT a.*,ci.merger_name mergerName from company_customer a LEFT JOIN city_area ci on a.city_area_id=ci.id) a
         LEFT JOIN (select u.* ,us.work,us.work_unit workUnit
        from user u left join user_basis us
        on u.id=us.user_id)u
        on a.user_id=u.id


        (u.name like '%${condition}%'
        or u.mobile like '%${condition}%'
        or a.mergerName like '%${condition}%'
         or a.address like '%${condition}%')


        AND a.company_id = #{companyId}


        AND a.user_id = #{userId}


        AND a.status = #{status}


        AND a.creator = #{creator}


        AND a.created = #{created}


        AND a.customer_manager = #{customerManager}


        AND a.grade = #{grade}


        AND a.source = #{source}


        AND community = #{community}


        AND a.city_area_id = #{cityAreaId}


        AND a.address = #{address}


        AND a.remarks = #{remarks}


        AND a.branch_id = #{branchId}


        AND a.name = #{name}


        AND a.phone = #{phone}

 SELECT count(ca.merger_name) count,ca.merger_name mergerName
 from (SELECT cc.user_id userId,u.city_area_id cityAreaId from  company_customer cc
LEFT JOIN user u on cc.user_id=u.id


         cc.company_id = #{companyId}


         AND cc.customer_manager = #{saleMan}


         AND date_format(cc.created,'%Y-%m')=#{selectTime}


     and u.city_area_id is not null
  )
      cc left  JOIN
    city_area ca on cc.cityAreaId=ca.id  GROUP BY ca.id

SELECT  from company_customer


        company_id = #{companyId}


        AND customer_manager = #{saleMan}


        AND date_format(created,'%Y-%m')=#{selectTime}


        AND name = #{name}

SELECT date_format(created,'%Y-%m-%d') creatTime,IFNULL(COUNT(*) ,0) as count  from company_customer


        company_id = #{companyId}


        AND customer_manager = #{saleMan}


        AND date_format(created,'%Y-%m')=#{selectTime}



    GROUP BY date_format(created,'%Y-%m-%d') order by created

select cc.name customer_name, cc.phone customer_mobile, c.name company_name, ca.merger_name customer_merger_name, cc.id customer_id, (case c.type WHEN 1 THEN '直营店' WHEN 2 THEN '店中店' WHEN 3 THEN '专卖店' WHEN 4 THEN '加盟店' ELSE '未知' END ) company_type,cc.created from company_customer cc left join company c on cc.company_id=c.id left join city_area ca on cc.city_area_id = ca.id
cc.city_area_id =#{cityId}
and cc.name like '%${condation}%' or cc.phone like '%${condation}%' or cc.id in (select customer_id from custom_order where no like '%${condation}%')
and cc.company_id = #{dealerId}
and c.branch_id = #{branchId}
and cc.user_id not in(SELECT DISTINCT(co.customer_id) from custom_order co where co.branch_id=#{branchId} and co.customer_id is not null)
and cc.user_id in (SELECT DISTINCT(co.customer_id) from custom_order co where co.branch_id=#{branchId} and co.status != '16' and co.customer_id is not null)
and cc.user_id in(SELECT DISTINCT(co.customer_id) from custom_order co where co.branch_id=#{branchId} and co.status='16' and co.customer_id is not null )

select cc.name customer_name, cc.phone customer_mobile, c.name company_name, ca.merger_name customer_merger_name, cc.id customer_id, cc.community, cc.created from company_customer cc left join company c on cc.company_id=c.id left join city_area ca on cc.city_area_id = ca.id
and cc.name like '%${condation}%' or phone like '%${condation}%'
and cc.company_id = #{companyId}
and cc.id not in(SELECT DISTINCT(co.customer_id) from custom_order co where co.company_id=#{companyId} and co.customer_id is not null)
and cc.id in (SELECT DISTINCT(co.customer_id) from custom_order co where co.company_id=#{companyId} and co.status != '16' and co.customer_id is not null)
and cc.id in(SELECT DISTINCT(co.customer_id) from custom_order co where co.company_id=#{companyId} and co.status='16'and co.customer_id is not null)
AND date_format(cc.created,'%Y-%m-%d') >= #{startTime}
AND date_format(cc.created,'%Y-%m-%d') <= #{endTime}

select cc.*, c.name as companyName,c.leader_tel as leaderTel,c.id as companyId,ca.merger_name as cityAreaName ,u.name customerManagerName, ca1.id cityId,ca1.parent_id provinceId from company_customer cc left join company c on c.id = cc.company_id left join city_area ca on ca.id = cc.city_area_id left join company_employee ce on ce.id = cc.customer_manager left join user u on ce.user_id = u.id left join city_area ca1 on ca.parent_id = ca1.id
cc.name like CONCAT('%',#{name},'%')
and cc.company_id = #{companyId}
and cc.phone like CONCAT('%',#{phone},'%')
and c.branch_id = #{branchId}
order by cc.created desc
