mapper.select:
SELECT c.*,ct.name as typeName
FROM   contents c
left join contents_type ct on ct.id = c.contents_type_id


    c.id = #{id}


    AND c.name = #{name}


    AND c.code = #{code}


    AND c.cover = #{cover}


    AND c.summary = #{summary}


    AND c.status = #{status}


    AND c.publish_time = #{publishTime}


    AND c.publisher = #{publisher}


    AND c.is_top = #{top}


    AND c.created = #{created}


    AND c.creator = #{creator}


        AND c.contents_type_id = #{contentsTypeId}

select cs.id,cs.name,cs.summary,cs.cover, cs.publish_time as created,u.name as userName,u.avatar as userAvatar from contents cs left join user u on u.id = cs.publisher
code = #{code}
and status = #{status}
order by is_top desc, publish_time desc
