SELECT

FROM custom_order



            id = #{id}


            AND no = #{no}


            AND customer_id = #{customerId}


            AND company_id = #{companyId}


            AND city_area_id = #{cityAreaId}


            AND address = #{address}


            AND acreage = #{acreage}


            AND status = #{status}


            AND salesman = #{salesman}


            AND merchandiser = #{merchandiser}


            AND creator = #{creator}


            AND created = #{created}


            AND designer = #{designer}


            AND design_scheme = #{designScheme}


            AND earnest = #{earnest}


            AND imprest = #{imprest}


            AND retainage = #{retainage}


            AND amount = #{amount}


            AND notes = #{notes}


            AND design_style = #{designStyle}


            AND type = #{type}


            AND parent_id = #{parentId}


            AND estimated_delivery_date = #{estimatedDeliveryDate}


            AND delivery_date = #{deliveryDate}


            AND design_fee = #{designFee}


            AND factory_price = #{factoryPrice}


            AND customer_tel = #{customerTel}


            AND market_price = #{marketPrice}


            AND discounts = #{discounts}


            AND factory_discounts = #{factoryDiscounts}


            AND factory_final_price = #{factoryFinalPrice}


            AND is_confirm_fprice = #{confirmFprice}


            AND is_confirm_cprice = #{confirmCprice}


            AND is_design = #{isDesign}


            AND time_consuming = #{timeConsuming}


            AND is_coordination = #{coordination}


            AND documentary_notes = #{documentaryNotes}


            AND documentary_time = #{documentaryTime}


            AND consignee_name = #{consigneeName}


            AND consignee_tel = #{consigneeTel}

SELECT
FROM custom_order o left join city_area c on o.city_area_id = c.id left join user us on us.id = o.customer_id
(o.no like '%${condition}%' or o.address like '%${condition}%' or o.notes like '%${condition}%' or o.customer_tel like '%${condition}%' or o.customer_id in (select id from user u where u.name like '%${condition}%' and u.id in (select c.user_id from company_customer c where c.company_id = #{companyId})))
and o.salesman = #{salesman}
and o.company_id = #{companyId}
and o.type = #{type}
order by o.created desc

select o.*, us.name as customerName, c.merger_name as cityName,u.name as salesmanName,u.mobile as salesmanTel,u.avatar as salesmanAvatar,uses.name designerName FROM (select o.*,c2.name dealerName,c2.leader_tel dealerTel,c2.city_area_id dealerCityAreaId,c2.address dealerAddress from custom_order o left join company c2 on o.company_id = c2.id) o left join city_area c on o.city_area_id = c.id left join user u on u.id = o.salesman left join user us on us.id = o.customer_id left join user uses on uses.id =o.designer where o.status in
#{statu}
and o.id not in (select o.id from custom_order o right join finished_stock f on o.id = f.order_id)

SELECT co.id, co.no, co.customer_id, co.company_id,co.amount, co.factory_price, co.customer_tel, co.market_price, co.discounts,co.created, co.factory_discounts, co.factory_final_price, co.is_confirm_fprice,co.is_confirm_cprice,co.customerAddress,u.name customerName ,u.mergerName from (SELECT co.*,cc.address customerAddress from custom_order co LEFT JOIN company_customer cc on co.company_id=cc.company_id and co.customer_id=cc.user_id )co LEFT JOIN (SELECT u.*,ci.merger_name mergerName from user u LEFT JOIN city_area ci on u.city_area_id=ci.id) u on co.customer_id=u.id
(co.no like '%${condition}%' or co.customer_tel like '%${condition}%' or u.name like '%${condition}%' or u.mergerName like '%${condition}%')
AND co.company_id=#{companyId}
AND co.is_confirm_fprice = #{confirmFprice}
order by co.created desc

select cod.name as demandName, o.id as orderId, o.status as orderStatus, o.no as orderNum, o.created as created, IFNULL(o.delivery_date,"暂未完成") as deliveryDate, u.name as designerName, u.mobile as designerMobile from custom_order_demand cod right join custom_order o on o.id = cod.custom_order_id left join user u on u.id = o.designer
and o.company_id = #{companyId}
and o.type = #{type}
and (o.status = ${status})
group by o.id
order by o.created desc

select o.*, us.name as customerName, c.merger_name as cityName,u.name as salesmanName,u.mobile as salesmanTel,u.avatar as salesmanAvatar,uses.name designerName,uu.name creatorName FROM (select o.*,c2.name dealerName,c2.leader_tel dealerTel,c2.city_area_id dealerCityAreaId,c2.address dealerAddress from custom_order o left join company c2 on o.company_id = c2.id
where c2.leader_tel like CONCAT('%',#{dealerTel},'%')
) o left join city_area c on o.city_area_id = c.id left join user u on u.id = o.salesman left join user us on us.id = o.customer_id left join user uses on uses.id = o.designer left join user uu on uu.id = o.creator
o.no like concat('%',#{no},'%')
AND o.customer_tel like concat('%',#{customerTel},'%')
AND o.status in
#{statu}
AND o.type = #{type}
AND o.company_id = #{companyId}
AND o.designer is null
AND o.designer is not null
AND o.designer = #{designer}

select count(*) from custom_order


        company_id = #{companyId}


        AND salesman = #{saleMan}


        AND status = #{orderStatus}


        AND date_format(created,'%Y-%m')=#{selectTime}

select count(*)as orderNum,IFNULL(SUM(amount),0)as orderMoney from custom_order


        company_id = #{companyId}


        AND salesman = #{saleMan}


        AND date_format(created,'%Y-%m')=#{selectTime}

select   from custom_order


        company_id = #{companyId}


        AND salesman = #{saleMan}


        AND date_format(created,'%Y-%m')=#{selectTime}

select date_format(created,'%Y-%m-%d') creatTime,IFNULL(COUNT(*) ,0) as count from custom_order


        company_id = #{companyId}


        AND salesman = #{saleMan}


        AND date_format(created,'%Y-%m')=#{selectTime}



GROUP BY date_format(created,'%Y-%m-%d') order by created

select count(*) from custom_order


        company_id = #{companyId}


        AND salesman = #{saleMan}


        AND  amount >= #{low}


        AND  amount < #{high}


        AND date_format(created,'%Y-%m')=#{selectTime}

select   from custom_order


        company_id = #{companyId}


        AND customer_id = #{customerId}


        AND date_format(created,'%Y-%m')=#{selectTime}


        AND status=#{status}

select count(id) from custom_order


        created like '%${created}%'


        and is_design = #{isDesign}

select o.id,o.no,o.created,c.name, o.estimated_delivery_date as deliveryDate, o.documentary_time as processTime from custom_order o left join company c on c.id = o.company_id
o.created like '%${created}%'
and o.is_design = #{isDesign}
