select
u.id,u.name,u.mobile,us.name as customerManager
from company_customer cc
left join user u on u.id = cc.user_id
left join user us on us.id = cc.customer_manager


		cc.company_id = #{companyId}


		and cc.status = #{status}


		and cc.grade = #{grade}


		and cc.customer_manager = #{customerManager}

select u.id,u.name,u.mobile,u.avatar,
csm.identity as identitys,csm.score as score
from company_share_member csm
left join user u on u.id = csm.user_id


		csm.company_id = #{companyId}


		and csm.status = #{status}


		and csm.identity = #{identity}


	and (u.name like '%${condition}%'
	or u.mobile like '%${condition}%')


order by csm.created desc
